import React, {useState, useEffect} from "react"

export default function Scoreboard() {
    const [gameData, setGameData] = useState([])

    useEffect(() => {
        fetch("https://api.scorebooklive.com/v2/games?date=2022-05-12&priority_order=true")
        .then(res => {
            if(!res.ok) {
                throw Error("Something went wrong")
            }
            return res.json()
        })
        .then(data => setGameData(data.data))
        .catch(err => console.error(err))
    }, [])

    console.log(gameData)

    function renderResults(data) {
        return data.map(matchup => {
            return matchup.game_teams.map(team => {
                return (
                    <div className="single-team">
                        <h2>{team.team.name}</h2>
                        <p>{team.team.state}</p>
                        <img src={team.team.image} />
                    </div>
                )
            })
        })
    }
    return (
        <>
            <p>{gameData.length > 0 ? renderResults(gameData) : null}</p>
        </>
    )
}





